QDebug operator&lt;&lt;(QDebug dbg, const Coordinate &amp;c)
{
    QDebugStateSaver saver(dbg);
    dbg.nospace() &lt;&lt; "(" &lt;&lt; c.x() &lt;&lt; ", " &lt;&lt; c.y() &lt;&lt; ")";

    return dbg;
}

char *alloc(int size)
{
    Q_ASSERT(size &gt; 0);
    char *ptr = new char[size];
    Q_CHECK_PTR(ptr);
    return ptr;
}

char *alloc(int size)
{
    char *ptr;
    Q_CHECK_PTR(ptr = new char[size]);  // WRONG
    return ptr;
}
