class Y: public QEnableSharedFromThis<Y>
{
public:
    QSharedPointer<Y> f()
    {
        return sharedFromThis();
    }
};

int main()
{
    QSharedPointer<Y> p(new Y());
    QSharedPointer<Y> y = p->f();
    Q_ASSERT(p == y); // p and q must share ownership
}

