Rectangle {
    color:"white"
    width: 300
    height: 500

    AudioEngine {
        id:audioengine

        AudioCategory {
            name: "sfx"
            volume: 0.8
        }

        AudioSample {
            name:"explosion"
            source: "explosion-02.wav"
        }

        Sound {
            name:"explosion"
            category: "sfx"
            PlayVariation {
                sample:"explosion"
            }
        }
    }

    MouseArea {
        anchors.fill: parent
        onPressed: {
            audioengine.categories["sfx"].volume = 0.5;
        }
    }
}
