ColumnLayout {
    Switch {
        text: qsTr("Wi-Fi")
        checked: Networking.wifiEnabled
        onClicked: Networking.wifiEnabled = checked
    }
    Switch {
        text: qsTr("Bluetooth")
        checked: Networking.bluetoothEnabled
        onClicked: Networking.bluetoothEnabled = checked
    }
}
