bool PrimeCounter::filterFunction(const Element &amp;element)
{
    // Filter for primes
    if (element &lt;= 1)
        return false;
    for (Element i = 2; i*i &lt;= element; ++i) {
        if (element % i == 0)
            return false;
    }
    return true;
}
