void aFunction(QPromise&lt;int&gt; &amp;promise)
{
    promise.setProgressRange(0, 100);
    int result = 0;
    for (int i = 0; i &lt; 100; ++i) {
        // computes some part of the task
        const int part = ... ;
        result += part;
        promise.setProgressValue(i);
    }
    promise.addResult(result);
}

