dataArray-&gt;reserve(10);
for (int i = 0; i &lt; 10; i++) {
    QBarDataRow *dataRow = new QBarDataRow(5);
    for (int j = 0; j &lt; 5; j++)
        (*dataRow)[j].setValue(myData-&gt;getValue(i, j));
