db:section.db:programlisting:
ColumnLayout {
    anchors.fill: parent
    anchors.margins: parent.border.width
    spacing: 0
    clip: true
    LegendItem {
        Layout.fillWidth: true
        Layout.fillHeight: true
        series: station1
        theme: barGraph.theme
    }
    LegendItem {
        Layout.fillWidth: true
        Layout.fillHeight: true
        series: station2
        theme: barGraph.theme
    }
    LegendItem {
        Layout.fillWidth: true
        Layout.fillHeight: true
        series: station3
        theme: barGraph.theme
    }
}

states: [
    State  {
        name: "selected"
        when: series.selectedBar != series.invalidSelectionPosition
        PropertyChanges {
            target: legendItem
            color: series.singleHighlightColor
        }
    },
    State  {
        name: "unselected"
        when: series.selectedBar == series.invalidSelectionPosition
        PropertyChanges {
            target: legendItem
            color: theme.labelBackgroundColor
        }
    }
]

MouseArea {
    id: mouseArea
    anchors.fill: legendItem
    onClicked: {
        if (legendItem.state === "selected") {
            series.selectedBar = series.invalidSelectionPosition
        } else {
            series.selectedBar = previousSelection
        }
    }
}

Connections {
    target: series
    function onSelectedBarChanged(position) {
        if (position !== series.invalidSelectionPosition) {
            previousSelection = position
        }
    }
}
