MouseArea {
    anchors.fill: parent
    onClicked: root.clicked()
    onPressed: {
        glow.visible = true
        animation1.start()
        animation2.start()
    }
}

cappuccinoButton.onClicked: {
    sideBar.currentCoffee = qsTr("Cappucino")
    sideBar.currentMilk = 7
    sideBar.currentCoffeeAmount = 3.5
    sideBar.coffeeSelected()
}

