class GSuggestCompletion : public QObject
{
    Q_OBJECT

public:
    explicit GSuggestCompletion(QLineEdit *parent = nullptr);
    ~GSuggestCompletion();
    bool eventFilter(QObject *obj, QEvent *ev) override;
    void showCompletion(const QVector&lt;QString&gt; &amp;choices);

public slots:

    void doneCompletion();
    void preventSuggest();
    void autoSuggest();
    void handleNetworkData(QNetworkReply *networkReply);

private:
    QLineEdit *editor = nullptr;
    QTreeWidget *popup = nullptr;
    QTimer timer;
    QNetworkAccessManager networkManager;
};

GSuggestCompletion::GSuggestCompletion(QLineEdit *parent): QObject(parent), editor(parent)
{
    popup = new QTreeWidget;
    popup-&gt;setWindowFlags(Qt::Popup);
    popup-&gt;setFocusPolicy(Qt::NoFocus);
    popup-&gt;setFocusProxy(parent);
    popup-&gt;setMouseTracking(true);

    popup-&gt;setColumnCount(1);
    popup-&gt;setUniformRowHeights(true);
    popup-&gt;setRootIsDecorated(false);
    popup-&gt;setEditTriggers(QTreeWidget::NoEditTriggers);
    popup-&gt;setSelectionBehavior(QTreeWidget::SelectRows);
    popup-&gt;setFrameStyle(QFrame::Box | QFrame::Plain);
    popup-&gt;setHorizontalScrollBarPolicy(Qt::ScrollBarAlwaysOff);
    popup-&gt;header()-&gt;hide();

    popup-&gt;installEventFilter(this);

    connect(popup, SIGNAL(itemClicked(QTreeWidgetItem*,int)),
            SLOT(doneCompletion()));

    timer.setSingleShot(true);
    timer.setInterval(500);
    connect(&amp;timer, SIGNAL(timeout()), SLOT(autoSuggest()));
    connect(editor, SIGNAL(textEdited(QString)), &amp;timer, SLOT(start()));

    connect(&amp;networkManager, SIGNAL(finished(QNetworkReply*)),
            this, SLOT(handleNetworkData(QNetworkReply*)));

}

bool GSuggestCompletion::eventFilter(QObject *obj, QEvent *ev)
{
    if (obj != popup)
        return false;

    if (ev-&gt;type() == QEvent::MouseButtonPress) {
        popup-&gt;hide();
        editor-&gt;setFocus();
        return true;
    }

    if (ev-&gt;type() == QEvent::KeyPress) {
        bool consumed = false;
        int key = static_cast&lt;QKeyEvent*&gt;(ev)-&gt;key();
        switch (key) {
        case Qt::Key_Enter:
        case Qt::Key_Return:
            doneCompletion();
            consumed = true;
            break;

        case Qt::Key_Escape:
            editor-&gt;setFocus();
            popup-&gt;hide();
            consumed = true;
            break;

        case Qt::Key_Up:
        case Qt::Key_Down:
        case Qt::Key_Home:
        case Qt::Key_End:
        case Qt::Key_PageUp:
        case Qt::Key_PageDown:
            break;

        default:
            editor-&gt;setFocus();
            editor-&gt;event(ev);
            popup-&gt;hide();
            break;
        }

        return consumed;
    }

    return false;
}

void GSuggestCompletion::showCompletion(const QVector&lt;QString&gt; &amp;choices)
{
    if (choices.isEmpty())
        return;

    const QPalette &amp;pal = editor-&gt;palette();
    QColor color = pal.color(QPalette::Disabled, QPalette::WindowText);

    popup-&gt;setUpdatesEnabled(false);
    popup-&gt;clear();

    for (const auto &amp;choice : choices) {
        auto item  = new QTreeWidgetItem(popup);
        item-&gt;setText(0, choice);
        item-&gt;setForeground(0, color);
    }

    popup-&gt;setCurrentItem(popup-&gt;topLevelItem(0));
    popup-&gt;resizeColumnToContents(0);
    popup-&gt;setUpdatesEnabled(true);

    popup-&gt;move(editor-&gt;mapToGlobal(QPoint(0, editor-&gt;height())));
    popup-&gt;setFocus();
    popup-&gt;show();
}

void GSuggestCompletion::doneCompletion()
{
    timer.stop();
    popup-&gt;hide();
    editor-&gt;setFocus();
    QTreeWidgetItem *item = popup-&gt;currentItem();
    if (item) {
        editor-&gt;setText(item-&gt;text(0));
        QMetaObject::invokeMethod(editor, "returnPressed");
    }
}

void GSuggestCompletion::autoSuggest()
{
    QString str = editor-&gt;text();
    QString url = gsuggestUrl.arg(str);
    networkManager.get(QNetworkRequest(url));
}

void GSuggestCompletion::handleNetworkData(QNetworkReply *networkReply)
{
    QUrl url = networkReply-&gt;url();
    if (networkReply-&gt;error() == QNetworkReply::NoError) {
        QVector&lt;QString&gt; choices;

        QByteArray response(networkReply-&gt;readAll());
        QXmlStreamReader xml(response);
        while (!xml.atEnd()) {
            xml.readNext();
            if (xml.tokenType() == QXmlStreamReader::StartElement)
                if (xml.name() == "suggestion") {
                    QStringRef str = xml.attributes().value("data");
                    choices &lt;&lt; str.toString();
                }
        }

        showCompletion(choices);
    }

    networkReply-&gt;deleteLater();
}

#include &lt;QLineEdit&gt;

class GSuggestCompletion;

class SearchBox: public QLineEdit
{
    Q_OBJECT

public:
    explicit SearchBox(QWidget *parent = nullptr);

protected slots:
    void doSearch();

private:
    GSuggestCompletion *completer = nullptr;

SearchBox::SearchBox(QWidget *parent): QLineEdit(parent)
{
    completer = new GSuggestCompletion(this);

    connect(this, SIGNAL(returnPressed()),this, SLOT(doSearch()));

    setWindowTitle("Search with Google");

    adjustSize();
    resize(400, height());
    setFocus();
}

void SearchBox::doSearch()
{
    completer-&gt;preventSuggest();
    QString url = gsearchUrl.arg(text());
    QDesktopServices::openUrl(url);
}
