#include &lt;QGuiApplication&gt;
#include &lt;QQuickView&gt;

int main(int argc, char *argv[])
{
    QGuiApplication app(argc, argv);

    QQuickView view;
    view.setSource(QUrl::fromLocalFile("application.qml"));
    view.show();

    return app.exec();
}

#include &lt;QGuiApplication&gt;
