import QtQuick 2.0 import QtQuick.Particles 2.0
Rectangle { width: 360 height: 540 color: "black" Text { text: "Left click to start/stop\nRight click to pause/unpause" color: "white" font.pixelSize: 24 } MouseArea { anchors.fill: parent acceptedButtons: Qt.LeftButton | Qt.RightButton onClicked: { if (mouse.button == Qt.LeftButton) particles.running = !particles.running else particles.paused = !particles.paused; } }
ParticleSystem { id: particles running: false }
ImageParticle { anchors.fill: parent system: particles source: "qrc:///particleresources/star.png" sizeTable: "../../images/sparkleSize.png" alpha: 0 colorVariation: 0.6 }
Emitter { anchors.fill: parent system: particles emitRate: 2000 lifeSpan: 2000 size: 30 sizeVariation: 10 } }
