#include &lt;QApplication&gt;
#include &lt;QWebEngineView&gt;

int main(int argc, char *argv[])
{
    QApplication app(argc, argv);
    QWebEngineView view;
    view.load(QUrl("https://qt-project.org/"));
    view.resize(1024, 750);
    view.show();
    return app.exec();
}
