#include "window.h"

#include &lt;QApplication&gt;
#include &lt;QGraphicsView&gt;

int main(int argc, char *argv[])
{
    QApplication app(argc, argv);

    QGraphicsScene scene;
    QGraphicsView view(&amp;scene);
    Window *w = new Window;
    scene.addItem(w);

    view.resize(400, 300);
    view.show();

    return app.exec();
}

#include &lt;QApplication&gt;

#include "window.h"

int main(int argc, char *argv[])
{
    QApplication app(argc, argv);
    Window window;
    window.show();
    return app.exec();
}
