class ScribbleArea : public QWidget
{
    Q_OBJECT

public:
    ScribbleArea(QWidget *parent = nullptr);

    bool openImage(const QString &amp;fileName);
    bool saveImage(const QString &amp;fileName, const char *fileFormat);
    void setPenColor(const QColor &amp;newColor);
    void setPenWidth(int newWidth);

    bool isModified() const { return modified; }
    QColor penColor() const { return myPenColor; }
    int penWidth() const { return myPenWidth; }

public slots:
    void clearImage();
    void print();

protected:
    void mousePressEvent(QMouseEvent *event) override;
    void mouseMoveEvent(QMouseEvent *event) override;
    void mouseReleaseEvent(QMouseEvent *event) override;
    void paintEvent(QPaintEvent *event) override;
    void resizeEvent(QResizeEvent *event) override;

private:
    void drawLineTo(const QPoint &amp;endPoint);
    void resizeImage(QImage *image, const QSize &amp;newSize);

    bool modified = false;
    bool scribbling = false;
    int myPenWidth = 1;
    QColor myPenColor = Qt::blue;
    QImage image;
    QPoint lastPoint;
};

ScribbleArea::ScribbleArea(QWidget *parent)
    : QWidget(parent)
{
    setAttribute(Qt::WA_StaticContents);
}

bool ScribbleArea::openImage(const QString &amp;fileName)
{
    QImage loadedImage;
    if (!loadedImage.load(fileName))
        return false;

    QSize newSize = loadedImage.size().expandedTo(size());
    resizeImage(&amp;loadedImage, newSize);
    image = loadedImage;
    modified = false;
    update();
    return true;
}

bool ScribbleArea::saveImage(const QString &amp;fileName, const char *fileFormat)
{
    QImage visibleImage = image;
    resizeImage(&amp;visibleImage, size());

    if (visibleImage.save(fileName, fileFormat)) {
        modified = false;
        return true;
    }
    return false;
}

void ScribbleArea::setPenColor(const QColor &amp;newColor)
{
    myPenColor = newColor;
}

void ScribbleArea::setPenWidth(int newWidth)
{
    myPenWidth = newWidth;
}

void ScribbleArea::clearImage()
{
    image.fill(qRgb(255, 255, 255));
    modified = true;
    update();
}

void ScribbleArea::mousePressEvent(QMouseEvent *event)
{
    if (event-&gt;button() == Qt::LeftButton) {
        lastPoint = event-&gt;position().toPoint();
        scribbling = true;
    }
}

void ScribbleArea::mouseMoveEvent(QMouseEvent *event)
{
    if ((event-&gt;buttons() &amp; Qt::LeftButton) &amp;&amp; scribbling)
        drawLineTo(event-&gt;position().toPoint());
}

void ScribbleArea::mouseReleaseEvent(QMouseEvent *event)
{
    if (event-&gt;button() == Qt::LeftButton &amp;&amp; scribbling) {
        drawLineTo(event-&gt;position().toPoint());
        scribbling = false;
    }
}

void ScribbleArea::paintEvent(QPaintEvent *event)
{
    QPainter painter(this);
    QRect dirtyRect = event-&gt;rect();
    painter.drawImage(dirtyRect, image, dirtyRect);
}

void ScribbleArea::resizeEvent(QResizeEvent *event)
{
    if (width() &gt; image.width() || height() &gt; image.height()) {
        int newWidth = qMax(width() + 128, image.width());
        int newHeight = qMax(height() + 128, image.height());
        resizeImage(&amp;image, QSize(newWidth, newHeight));
        update();
    }
    QWidget::resizeEvent(event);
}

void ScribbleArea::drawLineTo(const QPoint &amp;endPoint)
{
    QPainter painter(&amp;image);
    painter.setPen(QPen(myPenColor, myPenWidth, Qt::SolidLine, Qt::RoundCap,
                        Qt::RoundJoin));
    painter.drawLine(lastPoint, endPoint);
    modified = true;

    int rad = (myPenWidth / 2) + 2;
    update(QRect(lastPoint, endPoint).normalized()
                                     .adjusted(-rad, -rad, +rad, +rad));
    lastPoint = endPoint;
}

void ScribbleArea::resizeImage(QImage *image, const QSize &amp;newSize)
{
    if (image-&gt;size() == newSize)
        return;

    QImage newImage(newSize, QImage::Format_RGB32);
    newImage.fill(qRgb(255, 255, 255));
    QPainter painter(&amp;newImage);
    painter.drawImage(QPoint(0, 0), *image);
    *image = newImage;
}

void ScribbleArea::print()
{
#if defined(QT_PRINTSUPPORT_LIB) &amp;&amp; QT_CONFIG(printdialog)
    QPrinter printer(QPrinter::HighResolution);

    QPrintDialog printDialog(&amp;printer, this);

    if (printDialog.exec() == QDialog::Accepted) {
        QPainter painter(&amp;printer);
        QRect rect = painter.viewport();
        QSize size = image.size();
        size.scale(rect.size(), Qt::KeepAspectRatio);
        painter.setViewport(rect.x(), rect.y(), size.width(), size.height());
        painter.setWindow(image.rect());
        painter.drawImage(0, 0, image);
    }
#endif // QT_CONFIG(printdialog)
}

class MainWindow : public QMainWindow
{
    Q_OBJECT

public:
    MainWindow(QWidget *parent = nullptr);

protected:
    void closeEvent(QCloseEvent *event) override;

private slots:
    void open();
    void save();
    void penColor();
    void penWidth();
    void about();

private:
    void createActions();
    void createMenus();
    bool maybeSave();
    bool saveFile(const QByteArray &amp;fileFormat);

    ScribbleArea *scribbleArea;

    QMenu *saveAsMenu;
    QMenu *fileMenu;
    QMenu *optionMenu;
    QMenu *helpMenu;

    QAction *openAct;
    QList&lt;QAction *&gt; saveAsActs;
    QAction *exitAct;
    QAction *penColorAct;
    QAction *penWidthAct;
    QAction *printAct;
    QAction *clearScreenAct;
    QAction *aboutAct;
    QAction *aboutQtAct;
};

MainWindow::MainWindow(QWidget *parent)
    : QMainWindow(parent), scribbleArea(new ScribbleArea(this))
{
    setCentralWidget(scribbleArea);

    createActions();
    createMenus();

    setWindowTitle(tr("Scribble"));
    resize(500, 500);
}

void MainWindow::closeEvent(QCloseEvent *event)
{
    if (maybeSave())
        event-&gt;accept();
    else
        event-&gt;ignore();
}

void MainWindow::open()
{
    if (maybeSave()) {
        QString fileName = QFileDialog::getOpenFileName(this,
                                   tr("Open File"), QDir::currentPath());
        if (!fileName.isEmpty())
            scribbleArea-&gt;openImage(fileName);
    }
}

void MainWindow::save()
{
    QAction *action = qobject_cast&lt;QAction *&gt;(sender());
    QByteArray fileFormat = action-&gt;data().toByteArray();
    saveFile(fileFormat);
}

void MainWindow::penColor()
{
    QColor newColor = QColorDialog::getColor(scribbleArea-&gt;penColor());
    if (newColor.isValid())
        scribbleArea-&gt;setPenColor(newColor);
}

void MainWindow::penWidth()
{
    bool ok;
    int newWidth = QInputDialog::getInt(this, tr("Scribble"),
                                        tr("Select pen width:"),
                                        scribbleArea-&gt;penWidth(),
                                        1, 50, 1, &amp;ok);
    if (ok)
        scribbleArea-&gt;setPenWidth(newWidth);
}

void MainWindow::createActions()
{
    openAct = new QAction(tr("&amp;Open..."), this);
    openAct-&gt;setShortcuts(QKeySequence::Open);
    connect(openAct, &amp;QAction::triggered, this, &amp;MainWindow::open);

    const QList&lt;QByteArray&gt; imageFormats = QImageWriter::supportedImageFormats();
    for (const QByteArray &amp;format : imageFormats) {
        QString text = tr("%1...").arg(QString::fromLatin1(format).toUpper());

        QAction *action = new QAction(text, this);
        action-&gt;setData(format);
        connect(action, &amp;QAction::triggered, this, &amp;MainWindow::save);
        saveAsActs.append(action);
    }

    printAct = new QAction(tr("&amp;Print..."), this);
    connect(printAct, &amp;QAction::triggered, scribbleArea, &amp;ScribbleArea::print);

    exitAct = new QAction(tr("E&amp;xit"), this);
    exitAct-&gt;setShortcuts(QKeySequence::Quit);
    connect(exitAct, &amp;QAction::triggered, this, &amp;MainWindow::close);

    penColorAct = new QAction(tr("&amp;Pen Color..."), this);
    connect(penColorAct, &amp;QAction::triggered, this, &amp;MainWindow::penColor);

    penWidthAct = new QAction(tr("Pen &amp;Width..."), this);
    connect(penWidthAct, &amp;QAction::triggered, this, &amp;MainWindow::penWidth);

    clearScreenAct = new QAction(tr("&amp;Clear Screen"), this);
    clearScreenAct-&gt;setShortcut(tr("Ctrl+L"));
    connect(clearScreenAct, &amp;QAction::triggered,
            scribbleArea, &amp;ScribbleArea::clearImage);

    aboutAct = new QAction(tr("&amp;About"), this);
    connect(aboutAct, &amp;QAction::triggered, this, &amp;MainWindow::about);

    aboutQtAct = new QAction(tr("About &amp;Qt"), this);
    connect(aboutQtAct, &amp;QAction::triggered, qApp, &amp;QApplication::aboutQt);
}

void MainWindow::createMenus()
{
    saveAsMenu = new QMenu(tr("&amp;Save As"), this);
    for (QAction *action : qAsConst(saveAsActs))
        saveAsMenu-&gt;addAction(action);

    fileMenu = new QMenu(tr("&amp;File"), this);
    fileMenu-&gt;addAction(openAct);
    fileMenu-&gt;addMenu(saveAsMenu);
    fileMenu-&gt;addAction(printAct);
    fileMenu-&gt;addSeparator();
    fileMenu-&gt;addAction(exitAct);

    optionMenu = new QMenu(tr("&amp;Options"), this);
    optionMenu-&gt;addAction(penColorAct);
    optionMenu-&gt;addAction(penWidthAct);
    optionMenu-&gt;addSeparator();
    optionMenu-&gt;addAction(clearScreenAct);

    helpMenu = new QMenu(tr("&amp;Help"), this);
    helpMenu-&gt;addAction(aboutAct);
    helpMenu-&gt;addAction(aboutQtAct);

    menuBar()-&gt;addMenu(fileMenu);
    menuBar()-&gt;addMenu(optionMenu);
    menuBar()-&gt;addMenu(helpMenu);
}

bool MainWindow::maybeSave()
{
    if (scribbleArea-&gt;isModified()) {
       QMessageBox::StandardButton ret;
       ret = QMessageBox::warning(this, tr("Scribble"),
                          tr("The image has been modified.\n"
                             "Do you want to save your changes?"),
                          QMessageBox::Save | QMessageBox::Discard
                          | QMessageBox::Cancel);
        if (ret == QMessageBox::Save)
            return saveFile("png");
        else if (ret == QMessageBox::Cancel)
            return false;
    }
    return true;
}

bool MainWindow::saveFile(const QByteArray &amp;fileFormat)
{
    QString initialPath = QDir::currentPath() + "/untitled." + fileFormat;

    QString fileName = QFileDialog::getSaveFileName(this, tr("Save As"),
                               initialPath,
                               tr("%1 Files (*.%2);;All Files (*)")
                               .arg(QString::fromLatin1(fileFormat.toUpper()))
                               .arg(QString::fromLatin1(fileFormat)));
    if (fileName.isEmpty())
        return false;
    return scribbleArea-&gt;saveImage(fileName, fileFormat.constData());
}
