struct Point2D
{
    int x;
    int y;
};

Q_DECLARE_TYPEINFO(Point2D, Q_PRIMITIVE_TYPE);

class Point2D
{
public:
    Point2D() { data = new int[2]; }
    Point2D(const Point2D &amp;other) { ... }
    ~Point2D() { delete[] data; }

    Point2D &amp;operator=(const Point2D &amp;other) { ... }

    int x() const { return data[0]; }
    int y() const { return data[1]; }

private:
    int *data;
};

