create procedure inout_proc (INOUT param1 INT, OUT param2 INT)
BEGIN
    set param1 = param1 * 2;
    set param2 = param1 * param1;
END

QSqlDatabase db;
QSqlQuery query;
int i1 = 10, i2 = 0;
query.prepare("call qtestproc(?, ?)");
query.bindValue(0, i1, QSql::InOut);
query.bindValue(1, i2, QSql::Out);
query.exec();

